import './App.css';
import Box from '@mui/material/Box';
import React, {useState, useEffect } from 'react';



class App extends React.Component {
constructor(props){
super(props)
this.state = {
    breakCount: 3,
    sessionCount: 25,
    clockCount: 3,
    currentTimer: 'Session',
    timerState: 'pause',
    interval: undefined
}
this.time = this.time.bind(this)
this.reset =this.reset.bind(this)
this.playPause =this.playPause.bind(this)
this.increaseSess =this.increaseSess.bind(this)
this.decreaseSess =this.decreaseSess.bind(this)

}
  // const [breakCount,setBreakCount] = useState(3);
  // const [sessionCount, setSessionCount] = useState(25);
  // const [clockCount, setClockCount] = useState(3)
  // const [currentTimer, setTimer] = useState('Session')
  // const [timerState, setTimerState] = useState('pause');
  // const [interval, setInter] = useState(undefined);

time =(count) =>{ 
  let min = Math.floor(count/60)
  let sec = (count % 60) 
  sec = sec < 10 ? ('0'+sec) : sec;

  return `${min}:${sec}`
}
reset =() =>{
  this.setState({
    breakCount: 5,
    sessionCount: 25,
    clockCount: 50 * 60,
    currentTimer: 'Session',
    timerState: 'pause',
    interval: undefined
  })
}

playPause =()=>{
  const checking = ()=>{
    if (clockCount === 0) {(
          setTimer((currentTimer ==='Session') ? 'Break' : 'Session')
  )}
  }

  if (timerState === 'play'){
    clearInterval(interval)
    setTimerState('pause')
  }
  else{
    setTimerState('play')
    setInter(setInterval(()=> setClockCount(clockCount => clockCount -1),1000))
    setTimer("Break")
    }
    setClockCount(clockCount)
    console.log(clockCount)


    // if(sessionCount === 0 || breakCount ===  0 ){
    //   setTimer(() => (currentTimer ==='Break') ? 'Session' : 'Break')
    // }

    // else{
      // if(currentTimer ==="Session") { 


      // } 
      // else {
      //   setInter(setInterval(()=> setBreakCount(breakCount => breakCount -1) ,1000))
      //  }

  // }


}






 increaseSess=()=>{
  this.setState({
      sessionCount: sessionCount +1,
      clockCount: clockCount + 1
  })
    }

 decreaseSess=()=>{
      this.setState({
        sessionCount: sessionCount -1,
        clockCount: clockCount - 1
      })    
    }

 render(){
  return (
    <>  
    <div className='clock'>
    <div id="break-label">
       <h3 style={{padding: '1em'}}>Break Length</h3>
       <div id='break-length'>
         <Box sx={{ width: 200, height: 50, backgroundColor: 'black', opacity: [0.9, 0.8, 0.7], display: 'grid', placeContent:'center'}}>
         <p style={{color: 'white'}}> {breakCount}</p>
         </Box> 
       </div>
       <div className='btns'>
       <button type='button' id='break-increment' onClick={() => this.setState({ breakCount: breakCount +1})}>
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
            <path strokeLinecap="round" strokeLinejoin="round" d="M8.25 6.75L12 3m0 0l3.75 3.75M12 3v18" />
              </svg>
       </button>

       <button type= 'button' id='break-decrement' onClick={() =>{ if (breakCount !== 1) return this.setState({ breakCount: breakCount -1})}}>
           <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
           <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 17.25L12 21m0 0l-3.75-3.75M12 21V3" />
           </svg>
       </button>
       </div>
     </div>

     <div id='session-label'>
       <h3 style={{padding: '1em'}}>Session Length</h3>
       <div id='session-length'>
         <Box sx={{ width: 200, height: 50, backgroundColor: 'black', opacity: [0.9, 0.8, 0.7], display: 'grid', placeContent:'center'}}>
         <p style={{color: 'white'}}>{sessionCount}:00</p>
         </Box> 
       </div>

       <div className='btns'>
         <button type='button' id='session-increment' onClick={() => this.increaseSess}>
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
             <path strokeLinecap="round" strokeLinejoin="round" d="M8.25 6.75L12 3m0 0l3.75 3.75M12 3v18" />
             </svg>
         </button>
         <button type='button' id='session-decrement'onClick={()=> {if (this.state.sessionCount !== 1) return this.decreaseSess}}>
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
             <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 17.25L12 21m0 0l-3.75-3.75M12 21V3" />
             </svg>
         </button>
       </div>
     </div>

     <div id='time-label'>
       <h3 style={{padding: '1em'}}>{this.state.currentTimer}</h3>
       <div id='time-left'>
         <Box sx={{ width: 300, height: 80, backgroundColor: 'black', opacity: [0.9, 0.8, 0.7], display: 'grid', placeContent:'center'}}>
         <p style={{color: 'white'}}>{this.time(this.state.clockCount)}</p>
         </Box> 
       </div>
       <div className='btns'>
         <button id='start_stop' onClick={this.playPause}>
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
             <path strokeLinecap="round" strokeLinejoin="round" d="M21 7.5V18M15 7.5V18M3 16.811V8.69c0-.864.933-1.406 1.683-.977l7.108 4.061a1.125 1.125 0 010 1.954l-7.108 4.061A1.125 1.125 0 013 16.811z" />
             </svg>
         </button>
         <button id='reset' onClick={this.reset}>
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
             <path strokeLinecap="round" strokeLinejoin="round" d="M16.023 9.348h4.992v-.001M2.985 19.644v-4.992m0 0h4.992m-4.993 0l3.181 3.183a8.25 8.25 0 0013.803-3.7M4.031 9.865a8.25 8.25 0 0113.803-3.7l3.181 3.182m0-4.991v4.99" />
             </svg>
         </button>
       </div>
     </div>

    </div>


    </> 
   );

 }

}

export default App;
